mapper.select:
select image_id,name,width,height,url,save_name from IMAGE i where 1 = 1

	   and i.commodity_id = #{commodity_id} 


	   and i.topic_id = #{topic_id} 


	   and i.campaign_id = #{campaign_id}

mapper.delete:
delete from IMAGE i where 1=1

	   and i.commodity_id = #{commodity_id} 


	   and i.topic_id = #{topic_id} 


	   and i.campaign_id = #{campaign_id}
